import { ActivatedRoute } from '@angular/router';
import { FormGroup, FormBuilder } from '@angular/forms';
import { myProductService } from './../my-products.service';
import { Component, OnInit } from '@angular/core';
import { Product } from 'src/app/products';

@Component({
  selector: 'app-update-product',
  templateUrl: './update-product.component.html',
  styleUrls: ['./update-product.component.css']
})
export class UpdateProductComponent implements OnInit {
  product!:Product;
  name:string='';
  price:any;
  description:any;

  private routeParams = this.route.snapshot.paramMap;
  private FromRoute = String(this.routeParams.get('productname'));

  ngOnInit(): void {
    this.routeParams=this.route.snapshot.paramMap;
    this.FromRoute=String(this.routeParams.get('productname'));

  }
  constructor(private myProduct:myProductService,
    private route:ActivatedRoute){  }


  updateName(){
    this.product.name=this.name;
    this.myProduct.updateProduct('product/'+this.FromRoute,this.product).subscribe(res=>{});
  }
  updatePrice(){
    this.product.price=this.price;
    this.myProduct.updateProduct('product/'+this.FromRoute,this.product).subscribe(res=>{});
  }
  updateDesc(){
    this.product.description=this.description;
    this.myProduct.updateProduct('product/'+this.FromRoute,this.product).subscribe(res=>{});
  }
}
<form [formGroup]="form">
<div>
<input class="form-control" type="text" name="name" [(ngModel)]="name" placeholder="Updated Name">
<button (click)="updateName()">Update Name</button>
</div>

<div>
  <input class="form-control" type="number" [(ngModel)]="price" placeholder="Updated Price">
  <button (click)="updatePrice()">Update Price</button>
</div>

<div>
  <input class="form-control" type="text" [(ngModel)]="description" placeholder="Updated description">
  <button (click)="updateDesc()">Update Description</button>
</div>
</form>
