import { Product } from './../../products';
import { Component, OnInit } from '@angular/core';
import {ActivatedRoute, Router} from '@angular/router'
import { myProductService } from '../my-products.service';
@Component({
  selector: 'app-seller-dashboard',
  templateUrl: './seller-dashboard.component.html',
  styleUrls: ['./seller-dashboard.component.css']
})
export class SellerDashboardComponent implements OnInit{
  private products:Product[]=[]
  constructor(private route: ActivatedRoute,
    private router:Router,
    private myProducts:myProductService)
    {}
  endpoint:string="/sellers"

  ngOnInit() {
    const routeParams = this.route.snapshot.paramMap;
    const productIdFromRoute = Number(routeParams.get('productId'));

    this.myProducts.getMyProducts(this.endpoint).subscribe(products=>this.products=products)

  }
  addProducts(){
    this.router.navigate(['/sellers/:sellername/addProducts'])
  }



}
