import { User } from './../../User';
import { Router } from '@angular/router';
import { Component, OnInit } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { UserLoginService } from 'src/app/user-login.service';

@Component({
  selector: 'app-seller-login',
  templateUrl: './seller-login.component.html',
  styleUrls: ['./seller-login.component.css']
})
export class SellerLoginComponent implements OnInit{
  LoginForm = this.formBuilder.group({
    username: 'username',
    password: 'password'
  });
  token:String=""

  constructor(private formBuilder: FormBuilder,
    private router:Router,
    private login:UserLoginService){}


  onSubmit(data:User){
    this.user.username=data.username;
    this.user.password=data.password
    this.login.verifyLogin(this.user,'seller/login').subscribe(access_token=> this.token=access_token)
    if (this.token){
    this.router.navigate(['sellers/:sellername'/*logic to add sellername*/])
    }
    else{
      this.router.navigate(["/"])
    }
  }
  ngOnInit(): void {

  }
}

<div class="center">
<div ><h2>Login Credentials</h2></div>

<form [formGroup]="LoginForm" (ngSubmit)="onSubmit()">        
  <div>  
    <label for="inputUserName">UserName</label>
    <input class="form-control" type="text" 
            id="inputUsername"
            placeholder="Username"
            name="Username"
            formControlName="username"
            ngModel
          >
  </div>
  <div>
    <label for="inputPassword">Password</label>
    <input class="form-control" type="password" 
            id="inputPassword"
            placeholder="Password"
            name="password"
            formControlName="password"
            ngModel
          >
  </div>
  <button class="button" type="submit">Login</button>
  </form>
