import { Observable } from 'rxjs';
import { User } from './../User';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import { Component } from '@angular/core';
import { UserLoginService } from '../user-login.service';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  username:string="";
  password:string="";
  private user !:User;
  verify:boolean=false;
  //login_group=new FormGroup({username:new FormControl},{password: new FormControl});
  constructor(
      private router:Router,
      private http:HttpClient,
      private login:UserLoginService  ){}
  buyerLogin(){
    //this.http.get("127.0.0.1:8000/api/users",user){}
    this.router.navigate(['/buyer'])
  }
  sellerLogin(){
    this.user.username=this.username;
    this.user.password=this.password;
    this.login.verifyLogin(this.user,'/sellers/login').subscribe(verify=>this.verify=verify)
    if (this.verify){ 
      this.router.navigate(['/seller'])
    }
  }
}
<br>
<div class="center">
<div ><h2>Login Credentials</h2></div>


<form [form-group]="login_group">
  <label for="inputUserName">UserName</label>
  <input class="form-control" type="text" 
        id="inputUserName"
        placeholder="UserName"
        name="UserName"
        formControlName="username"
        [(ngModel)]="username"
      >

  <label for="inputPassword">Password</label>
  <input class="form-control" type="text" 
        id="inputPassword"
        placeholder="Password"
        name="password"
        formControlName="password"
        [(ngModel)]="password"
      >
</form>


        <button (click)="buyerLogin()" class="button">buyer-login</button>

        <button (click)="sellerLogin()" class="button">seller-login</button>
        <br><br>

</div>
