import { PreviousOrderComponent } from './../../buyer/previous-order/previous-order.component';
import { Product } from 'src/app/products';
import { myProductService } from './../my-products.service';
import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router'
import { FormGroup, FormBuilder } from '@angular/forms';
@Component({
  selector: 'app-add-products',
  templateUrl: './add-products.component.html',
  styleUrls: ['./add-products.component.css']
})
export class AddProductsComponent {


  AddProduct !: FormGroup;
  product!:Product;
  endpoint:string="product";
  constructor(
    private router:Router,
    private route: ActivatedRoute,
    private productService:myProductService,
    private fb:FormBuilder
  ){
    this.AddProduct=this.fb.group({
      productname:"",
      price:0,
      description:""
    })
  }
  routeParams = this.route.snapshot.paramMap;
  FromRoute = String(this.routeParams.get('sellername'));
  addProduct(){
  this.product.name=this.AddProduct.value.productname
  this.product.price=this.AddProduct.value.price
  this.product.description=this.AddProduct.value.description
  this.product.sellername=this.FromRoute
  this.productService.addNewProduct(this.endpoint,this.product) 
  this.router.navigate(['/seller'])
 }
}
